    private void jPanel1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jPanel1MouseClicked
        if (evt.getClickCount() == 2) {
            mapProcessor.adjustShowCenter(evt.getX(), evt.getY());
            ImageIcon Icon = mapProcessor.adjustScale(jSlider1.getValue());
            jPanel2.setSize(241, 194);
            jPanel2.removeAll();
            JLabel jl = new JLabel();
            jl.setLayout(null);
            jl.setLocation(0, 0);
            jl.setSize(241, 194);
            jl.setIcon(Icon);
            jPanel2.add(jl);
            SwingUtilities.updateComponentTreeUI(jl);
        } else if (evt.getButton() == evt.BUTTON3) {
            DefaultListModel m1 = (DefaultListModel) (jList1.getModel());
            m1.clear();
            for (int i = 0; i < aList.size(); i++) {
                xDistance = Math.abs(evt.getX() - aList.get(i).getCoordinate().getX());
                yDistance = Math.abs(evt.getY() - aList.get(i).getCoordinate().getY());
                if ((xDistance < 10) && (yDistance < 10)) {
                    m1.addElement(aList.get(i).toString());
                }
            }
            for (int i = 0; i < hList.size(); i++) {
                xDistance = Math.abs(evt.getX() - hList.get(i).getCoordinate().getX());
                yDistance = Math.abs(evt.getY() - hList.get(i).getCoordinate().getY());
                if ((xDistance < 10) && (yDistance < 10)) {
                    m1.addElement(hList.get(i).toString());
                }
            }
        }

    }//GEN-LAST:event_jPanel1MouseClicked

    private void searchButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_searchButtonActionPerformed
        DefaultListModel m1 = (DefaultListModel) (jList1.getModel());
        m1.clear();
        for (int i = 0; i < aList.size(); i++) {
            if (searchText.getText().equals(aList.get(i).getName())) {
                m1.addElement(aList.get(i).toString());
            }
        }
        for (int i = 0; i < hList.size(); i++) {
            if (searchText.getText().equals(hList.get(i).getName())) {
                m1.addElement(hList.get(i).toString());
            }
        }
        if (m1.isEmpty()) {
            JOptionPane.showMessageDialog(null, "Sorry, cannot find the place you are looking for.");
        }// TODO add your handling code here:
    }//GEN-LAST:event_searchButtonActionPerformed

