    private void generateButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_generateButtonActionPerformed
        //LinkedList<Attraction> plan;
        RouteProcessor = new RouteProcessor();
        DefaultListModel m2 = (DefaultListModel) (jList2.getModel());
        m2.clear();
        DefaultListModel m1 = (DefaultListModel) (jList1.getModel());
        m1.clear();
        String planAttractions = "Route is:";
        Double planBudget = 0.0;
        int planDays = 0;
        String weather = "Weather will be:";
        if (((startLocationText.getText()).length() < 1) || ((destinationText.getText()).length() < 1)) {
            JOptionPane.showMessageDialog(null, "Sorry, please fill in both start location and destination.");
        } else {
            if (budgetText.getText().length() < 1) {
                RouteProcessor.setBudget(9999999);
            } else {
                RouteProcessor.setBudget(Double.parseDouble(budgetText.getText()));
            }
            for (Attraction a : aList) {
                if ((a.getName()).equals(destinationText.getText())) {
                    RouteProcessor.setDestination(a);
                    break;
                }
            }
            for (Attraction a : aList) {
                if ((a.getName()).equals(startLocationText.getText())) {
                    RouteProcessor.setStart(a);
                    break;
                }
            }
            RouteProcessor.setAttractionPerference(attractionsPerferences.getSelectedItem().toString());

            if (routePerferences.getSelectedIndex() == 1) {
                plan = RouteProcessor.limitedBudgetPlan(RouteProcessor.lowestPriceRoute());
            }
            if (routePerferences.getSelectedIndex() == 2) {
                plan = RouteProcessor.limitedBudgetPlan(RouteProcessor.leastDaysRoute());
            }
            if (routePerferences.getSelectedIndex() == 0) {
                plan = RouteProcessor.noChoiceRoute();
            }
            for (Attraction a : plan) {
                m1.addElement(a.toString());
                planAttractions = planAttractions + a.getName() + " ";
                planBudget = planBudget + a.getBudget();
                planDays = planDays + a.getDay();
                weather = weather + a.getName() + ":" + a.getWeather() + ".\n";
            }
            m2.addElement(planAttractions);
            m2.addElement("Total budget is: " + planBudget + "\n");
            m2.addElement("Total cost:" + planDays + " days.\n");
            m2.addElement(weather);
            Graphics g = jPanel1.getGraphics();
            for (int l = 0; l < plan.size() - 1; l++) {
               g.setColor(Color.yellow);
               g.drawLine(plan.get(l).getCoordinate().getX(), plan.get(l).getCoordinate().getY(), plan.get(l + 1).getCoordinate().getX(), plan.get(l + 1).getCoordinate().getY());
            }
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_generateButtonActionPerformed

    private void modifyButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_modifyButtonActionPerformed
        DefaultListModel m1 = (DefaultListModel) (jList1.getModel());
        m1.clear();
        DefaultListModel m2 = (DefaultListModel) (jList2.getModel());
        m2.clear();
        String planAttractions = "Route is:";
        Double planBudget = 0.0;
        int planDays = 0;
        String weather = "Weather will be:";
        int n = jList1.getSelectedIndex();
        plan.remove(n);
        for (Attraction a : plan) {
            m1.addElement(a.toString());
            planAttractions = planAttractions + a.getName() + " ";
            planBudget = planBudget + a.getBudget();
            planDays = planDays + a.getDay();
            weather = weather + a.getName() + ":" + a.getWeather() + ".\n";
        }
        m2.addElement(planAttractions);
        m2.addElement("Total budget is: " + planBudget + "\n");
        m2.addElement("Total cost:" + planDays + " days.\n");
        m2.addElement(weather);

    }//GEN-LAST:event_modifyButtonActionPerformed

